import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';


const UserAccount = () => {
    const [formData, setFormData] = useState({
        favoriteGenres: [],
        favoriteActors: [],
        preferredLanguage: [],
        preferredNews: [],
    });
    const findNews = async(data) => {
        //const response = await axios.post('http://localhost:3001/generateNews', data);
        //console.log(response.data);
        //console.log(typeof response.data[0]);
    }
    const location = useLocation();
    const username = location.state.username;
    const favoriteGenres = location.state.favoriteGenres;
    const favoriteActors = location.state.favoriteActors;
    const preferredLanguage = location.state.preferredLanguage;
    const preferredNews = location.state.preferredNews;
    console.log("User Account Check");
    console.log(favoriteGenres);
    console.log(favoriteActors);
    console.log(preferredLanguage);
    console.log(preferredNews);
    useEffect(() => {
        const newFormData = {...formData, 
            favoriteGenres: favoriteGenres, 
            favoriteActors: favoriteActors, 
            preferredLanguage: preferredLanguage, 
            preferredNews: preferredNews
        };
        findNews(newFormData);

    }, [location]);

    const headline = `"Variety and iHeartRadio Team Up for New Podcast: 'Variety Confidential'"`
    const body = `Variety and iHeartRadio have announced their collaboration on a new podcast titled 
    "Variety Confidential." This joint venture aims to bring listeners exclusive interviews 
    and insights from some of the biggest names in entertainment. Hosted by Variety's 
    executive editor, Ramin Setoodeh, and iHeartRadio's chief programming officer, Tom Poleman, 
    the podcast will cover a wide range of topics including film, television, music, and more.
    In addition to this exciting announcement, the companies also revealed that the podcast 
    will be available on all major streaming platforms. This includes iHeartRadio, 
    Apple Podcasts, Spotify, and more. Fans can expect to hear from notable guests 
    such as George Clooney, Sylvester Stallone, and Dakota Fanning, just to name a few.
    With the first episode set to air next week, fans are eagerly anticipating the launch 
    of "Variety Confidential." This collaboration between two media giants is sure to bring 
    a fresh perspective to the entertainment industry and keep listeners informed and 
    entertained. Stay tuned for more updates on this exciting new podcast.
    `
    return(
        <Slide>
            <div className="each-slide-effect">
                <div className="flex justify-center text-4xl font-bold">
                    <span>{headline}</span>
                </div>
                <div>
                    <span>{body}</span>
                </div>
            </div>
    
            <div className="each-slide-effect">
            <div>
                    <span>{body}</span>
                </div>
            </div>
        </Slide>
    )
    
}

export default UserAccount;
