import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const [formData, setFormData] = useState({
    favoriteGenres: [],
    favoriteActors: [],
    preferredLanguage: [],
    preferredNews: [],
});

const UserAccount = async(e) => {
    const location = useLocation();
    const username = location.state.username;
    const favoriteGenres = location.state.favoriteGenres;
    const favoriteActors = location.state.favoriteActors;
    const preferredLanguage = location.state.preferredLanguage;
    const preferredNews = location.state.preferredNews;
    setFormData({...formData, favoriteGenres: favoriteGenres, favoriteActors: favoriteActors, preferredLanguage: preferredLanguage, preferredNews: preferredNews});
    const response = await axios.post('http://localhost:3001/generateNews', formData);
}

export default UserAccount;
